import axios from "axios"


const getList = (list)=>({
    type : 'GET_LIST',
    payload : list
})

const deleteRow = ()=>({
    type : 'DEL_ROW',
    payload : list
})

export const loadList = ()=>{

    return function (dispatch){
        axios.get('/get-todo')
            .then((res)=>{
                dispatch(getList(res.data))
            })
            .catch((err)=>console.log(err))
    }
}

export const deleteToDo = (_id)=>{
    axios
        .post('/delete-todo', {_id})
        .then((res)=>{
            console.log(res.data)
        })
        .catch((err)=>console.log(err))

    return {
        type: 'DELETE_TEXT'
    }
}





export const addupdate = (text, update, setText, setUpdate)=>{

    if(update == ''){
        axios
            .post('/save-todo', {text})
            .then((res)=>{
                console.log(res.data)
                setText('')
            })
            .catch((err)=>console.log(err))

        return {
            type: 'ADD_TEXT'
        }

    } else {
        axios
            .post('/update-todo', {_id:update, text})
            .then((res)=>{
                console.log(res.data)
                setText('')
                setUpdate('')
            })
            .catch((err)=>console.log(err))

        return {
            type: 'UPDATE_TEXT'
        }
    }
}

export const editToDo = (_id, text, setText, setUpdate)=>{
    setUpdate(_id)
    setText(text)

    return {
        type: 'EDIT_HELP_UPDATE'
    }
}
