import store from '../../Redux/store'
import { useState } from 'react'
import axios from 'axios'



export default (text, editData, setEditCheck, storedata, setEdit)=>{

    const [id, setId] = useState('')

    const addform = async (e)=>{

        e.preventDefault()

        if(text == ''){
            return
        }

        store.dispatch({
            type: 'ADD_DATA',
            payload: text 
        })

        // try {

        //     await axios.post('/add', text)
        // } catch (error) {
        //     alert('DataBase Not Available')
        // }

    }


    const editform = (e)=>{

        e.preventDefault()

        if(editData == ''){
            return
        }

        storedata[id] = editData

        store.dispatch({
            type: 'EDIT_ROW',
            payload: storedata
        })

        setEditCheck(false)

    }

    const editrow = (evt, indx)=>{

        setEditCheck(true)
        const a = storedata[indx]
        setEdit(a)
        setId(indx)
    }

    const delrow = (evt, indx)=>{
        storedata.splice(indx,1)

        store.dispatch({
            type: 'DEL_ROW',
            payload: storedata
        })
    }


    return {
        addform,
        editform,
        editrow,
        delrow

    }
}
