import axios from "axios"


const getList = (list)=>({
    type : 'GET_LIST',
    payload : list
})

export const loadList = ()=>{

    return function (dispatch){
        axios.get('/get-todo')
            .then((res)=>{
                console.log(res.data);
                dispatch(getList(res.data))
            })
            .catch((err)=>console.log(err))
    }
}

