import '../../App.css'
import { useState } from 'react'
import store from '../../Redux/store'
import {useForm} from 'react-hook-form'
import {useSelector} from 'react-redux'
import useHome from '../Hooks/useHome'


export default ()=>{

    const storedata = useSelector(abc=>abc.datareducer.textData) || []

    const [editCheck, setEditCheck] = useState(false)
    const [addData, setAdd] = useState('')
    const [editData, setEdit] = useState('')
    const [a, setA] = useState(null)
    // const [editCheck, setEditCheck] = useState('')

    const formHandler = (e)=>{

        e.preventDefault()

        a ? store.dispatch({
            type: 'ADD_DATA',
            payload: addData 
        }) : store.dispatch({
            type: 'EDIT_ROW',
            payload: editData 
        })

        setA(null)  

    }


    const editrow = (evt, indx)=>{

        setEditCheck(true)
        const a = storedata[indx]
        setEdit(a)

    }

    const delrow = (evt, indx)=>{
        storedata.splice(indx,1)

        store.dispatch({
            type: 'DEL_ROW',
            payload: storedata
        })
    }


return <>
        <h2>
            TODO-LIST with Mern/Redux
        </h2>

        {
            editCheck ? <div>
                <h3>
                    Update DATA
                </h3>
                <form onSubmit={formHandler}>
                    <div className='form-control'>
                        <input type='text' placeholder='Enter Text...' onChange={(e)=> setEdit(e.target.value)} value={editData}/>
                    </div>
                    <button className='btn'> Update Data </button>
                </form>
            </div> : <div>
                <h3>
                    ADD DATA
                </h3>
                <form onSubmit={formHandler}>
                    <div className='form-control'>
                        <input type='text' placeholder='Enter Text...' onChange={(e)=> setAdd(e.target.value)} value={addData}/>
                    </div>
                    <button className='btn' onClick={()=>setA(33)}> Add Data </button>
                </form>
            </div>
        }


        <div>

        <h3>HISTORY</h3>
            <ul className="list">
            {
                storedata.map((ab, indx)=>{

                return <li key={indx}>
                    {ab} <button onClick={(evt)=>editrow(evt, indx)} className='edit-btn'> Edit </button>
                    <button onClick={(evt)=>delrow(evt, indx)} className='delete-btn'> X </button>
                </li>
                })
            }
            </ul>
        </div>
    </>
}







































































