import {createStore, combineReducers} from 'redux'


let userdata = {
    users:[],
    currentuser: {}
}

function userReducer(olddata = userdata, newdata){
    olddata = {users: [...olddata.users] , currentuser: {...olddata.currentuser}}

    switch (newdata.type) {

        case 'USER_LOGIN' :
            olddata.currentuser = newdata.payload
            break;

        case 'LOGOUT' :
            olddata.currentuser = {}
            localStorage.removeItem('token')
            break;

        default:
            break;
    }

    return olddata
}

let data={
    textData:[]
}
const datareducer = (olddata=data, newdata)=>{
    olddata = {textData : [...olddata.textData] }
    switch(newdata.type){
        case 'ADD_DATA':
            olddata.textData.push(newdata.payload)
            break;
        case 'DEL_ROW':
            return {...olddata, textData:[...newdata.payload]}
            break;
        case 'EDIT_ROW':
            return {...olddata, textData:[...newdata.payload]}
            break;
        default:
            break;
    }
    return olddata
}






let combine = combineReducers({userReducer, datareducer})
let store = createStore(combine)

export default store;
