import { useState } from "react"



export default ()=>{

    const addform = (e)=>{

        e.preventDefault()

        if(addData == ''){
            return
        }

        store.dispatch({
            type: 'ADD_DATA',
            payload: addData 
        })

    }


    return {
        addform,

    }
}
