import '../../App.css'
import { useEffect, useState } from 'react'
import useTodo from '../Hooks/useToDo'
import { useSelector } from 'react-redux'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'


export default ()=>{

    const storedata = useSelector(abc=>abc.datareducer.textData) || []
    const [todo, setTodo] = useState([])
    const [update, setUpdate] = useState('')

    const [editCheck, setEditCheck] = useState(false)
    const [text, setText] = useState('')
    const [editData, setEdit] = useState('')

    let loguser = useSelector((abc)=>{
        return (abc.userReducer.loginuser || '')
      })

    const nav = useNavigate()


    const {addform, editform, editrow, delrow} = useTodo(text, editData, setEditCheck, storedata, setEdit)

    useEffect(()=>{
        axios.get('/get-todo')
            .then((res)=>setTodo(res.data))
            .catch((err)=>console.log(err))



    }, [])

    const addupdate = (e)=>{

        e.preventDefault()



        if(update == ''){
            axios.post('/save-todo', {text})
                .then((res)=>{
                    console.log(res.data)
                    setText('')
                })
                .catch((err)=>console.log(err))
        } else {
            axios.post('/update-todo', {_id:update, text})
                .then((res)=>{
                    console.log(res.data)
                    setText('')
                    setUpdate('')
                })
                .catch((err)=>console.log(err))
        }    

    }

    const editToDo = (_id, text)=>{
        setUpdate(_id)
        setText(text)
    }
    const deleteToDo = (_id)=>{
        axios
            .post('/delete-todo', {_id})
            .then((res)=>{
                console.log(res.data)
            })
            .catch((err)=>console.log(err))
    }


return <>
        <h2>
            TODO-LIST with Mern/Redux
        </h2>

        {/* {
            editCheck ? <div>
                <h3>
                    Update DATA
                </h3>
                <form onSubmit={editform}>
                    <div className='form-control'>
                        <input type='text' placeholder='Enter Text...' onChange={(e)=> setEdit(e.target.value)} value={editData}/>
                    </div>
                    <button className='btn'> Update Data </button>
                </form>
            </div> : <div>
                <h3>
                    ADD DATA
                </h3>
                <form onSubmit={addform}>
                    <div className='form-control'>
                        <input type='text' placeholder='Enter Text...' onChange={(e)=> setText(e.target.value)} value={text}/>
                    </div>
                    <button className='btn'> Add Data </button>
                </form>
            </div>
        } */}


            <div>
                <h3>
                    {update ? 'UPDATE DATA' : 'ADD DATA'}
                </h3>
                <form onSubmit={addupdate}>
                    <div className='form-control'>
                        <input type='text' placeholder='Enter Text...' onChange={(e)=> setText(e.target.value)} value={text}/>
                    </div>
                    <button className='btn'> {update ? 'UPDATE DATA' : 'ADD DATA'}</button>
                </form>
            </div>


        {/* <div>

        <h3>HISTORY</h3>
            <ul className="list">
            {
                storedata.map((ab, indx)=>{

                return <li key={indx}>
                    {ab} <button onClick={(evt)=>editrow(evt, indx)} className='edit-btn'> Edit </button>
                    <button onClick={(evt)=>delrow(evt, indx)} className='delete-btn'> X </button>
                </li>
                })
            }
            </ul>
        </div> */}

        <div>

        <h3>HISTORY</h3>
            <ul className="list">
            {
                todo.map((item)=>{

                return <li key={item._id}>
                    {item.text} <button className='edit-btn' onClick={()=>editToDo(item._id, item.text)}> Edit </button>
                    <button className='delete-btn' onClick={()=>deleteToDo(item._id)}> X </button>
                </li>
                })
            }
            </ul>
        </div>
    </>
}
