const initialData={
    list:[]
}


const listReducer = (state=initialData, action)=>{
    // state = {list : [...state.list] }
    switch(action.type){
        case 'GET_LIST':
            return {
                ...state,
                list: action.payload
            }
        case 'ADD_DATA':
            state.list.push(action.payload)
            ;
        case 'DEL_ROW':
            return {...state, list:[...action.payload]}
            ;
        case 'EDIT_ROW':
            return {...state, list:[...action.payload]}
            ;
        default:
            return state;
    }
    return state
}

export default listReducer
